-- set raise alert UPDATE `problem` SET problemraisetypeId=5 WHERE `pro_date_raised`>='2016-01-01' AND `pro_problemno` IN ( SELECT `caa_problemno` FROM `callactivity` WHERE problem.`pro_problemno`= callactivity.`caa_problemno` AND `caa_callacttypeno`=57 AND callactivity.caa_serverguard = 'Y' );
-- set raise sales UPDATE `problem` SET problemraisetypeId=6 WHERE `pro_date_raised`>='2016-01-01' AND `pro_linked_ordno` IS NOT NULL AND `pro_priority` = 5;
-- set raise email UPDATE `problem` SET problemraisetypeId=1 WHERE `pro_date_raised`>='2016-01-01' AND `pro_problemno` IN ( SELECT `caa_problemno` FROM `callactivity` WHERE problem.`pro_problemno`= callactivity.`caa_problemno` AND callactivity.`caa_consno`=67 AND callactivity.`caa_callacttypeno` = 57 AND callactivity.caa_serverguard = 'N' );
-- set raise to Phone UPDATE `problem` SET problemraisetypeId=3 WHERE problemraisetypeId IS NULL AND `pro_date_raised`>='2016-01-01' AND `pro_problemno` IN ( SELECT `caa_problemno` FROM `callactivity` JOIN `consultant` ON callactivity.`caa_consno`=consultant.`cns_consno` WHERE problem.`pro_problemno`= callactivity.`caa_problemno` AND `caa_callacttypeno` = 57 AND `teamID`=1 AND `basedAtCustomerSite` = 0 );
-- manual UPDATE `problem` SET problemraisetypeId=7 WHERE problemraisetypeId IS NULL AND `pro_date_raised`>='2016-01-01' AND `pro_problemno` IN ( SELECT `caa_problemno` FROM `callactivity` JOIN `consultant` ON callactivity.`caa_consno`=consultant.`cns_consno` WHERE problem.`pro_problemno`= callactivity.`caa_problemno` AND `caa_callacttypeno` = 57 AND `teamID`>1 );
