update consultant
set basedAtCustomerSite=1
where cns_consno in (141, 132, 144, 131);

-- clear emraisetypeId UPDATE `problem` SET raiseTypeId=NULL;
-- set raise alert UPDATE `problem` SET raiseTypeId=5 WHERE `pro_date_raised` >= '2016-01-01' AND `pro_problemno` IN ( SELECT `caa_problemno` FROM `callactivity` WHERE problem.`pro_problemno` = callactivity.`caa_problemno` AND `caa_callacttypeno` = 51 AND callactivity.caa_serverguard = 'Y' );
-- set raise sales UPDATE `problem` SET raiseTypeId=6 WHERE `pro_date_raised` >= '2016-01-01' AND `pro_linked_ordno` IS NOT NULL AND `pro_priority` = 5 AND raiseTypeId IS NULL;
-- set raise email UPDATE `problem` SET raiseTypeId=1 WHERE `pro_date_raised` >= '2016-01-01' AND raiseTypeId IS NULL AND `pro_problemno` IN ( SELECT `caa_problemno` FROM `callactivity` WHERE problem.`pro_problemno` = callactivity.`caa_problemno` AND callactivity.`caa_consno` = 67 AND callactivity.`caa_callacttypeno` = 51 AND callactivity.caa_serverguard = 'N' );
-- set raise to Phone UPDATE `problem` SET raiseTypeId=3 WHERE raiseTypeId IS NULL AND `pro_date_raised` >= '2016-01-01' AND `pro_problemno` IN ( SELECT `caa_problemno` FROM `callactivity` JOIN `consultant` ON callactivity.`caa_consno` = consultant.`cns_consno` WHERE problem.`pro_problemno` = callactivity.`caa_problemno` AND `caa_callacttypeno` = 51 AND `teamID` = 1 AND `basedAtCustomerSite` = 0 );
-- manual UPDATE `problem` SET raiseTypeId=7 WHERE raiseTypeId IS NULL AND `pro_date_raised` >= '2016-01-01' AND `pro_problemno` IN ( SELECT `caa_problemno` FROM `callactivity` JOIN `consultant` ON callactivity.`caa_consno` = consultant.`cns_consno` WHERE problem.`pro_problemno` = callactivity.`caa_problemno` AND `caa_callacttypeno` = 51 AND `teamID` > 1 );
-- set raise to On Site UPDATE problem SET raiseTypeId=4 WHERE raiseTypeId IS NULL AND pro_date_raised >= '2016-01-01' AND pro_problemno IN ( SELECT caa_problemno FROM callactivity JOIN consultant ON callactivity.caa_consno = consultant.cns_consno WHERE problem.pro_problemno = callactivity.caa_problemno AND caa_callacttypeno = 51 AND teamID = 1 AND basedAtCustomerSite = 1 );

